import re

def extraire_emotions_mots():
    texte = """
    LE BONHEUR
    (avec être)
    heureux-se
    joyeux-se
    excité-e
    comblé-e
    à l'aise
    content-e
    calme
    réconforté-e
    amoureux-se
    fier-ère
    serein-e
    enthousiaste
    passionné-e
    en extase
    affectueux-se
    radieux-se
    rayonnant-e
    optimiste
    animé-e
    LE MALHEUR
    (avec être)
    malheureux-se
    bouleversé-e
    perdu-e
    troublé-e
    triste
    souffrant-e
    déçu-e
    en deuil, endeuillé-e
    confus-e
    fatigué-e
    blessé-e
    vulnérable
    sous le choc
    désespéré-e
    déprimé-e
    dépressif-ve (plus médical)
    suicidaire
    (plus je veux me suicider)
    trahi-e
    trompé-e
    mal à l'aise
    résigné-e
    pessimiste
    crevé-e
    déchiré-e
    embrouillé-e
    étourdi-e
    désorienté-e
    LA COLÈRE
    (avec être)
    fâché-e
    en colère
    en maudit (Qc FAM)
    choqué (Qc FAM)
    frustré-e
    impatient-e
    furieux-se
    enragé-e
    exaspéré-e
    vexé-e
    dégoûté-e
    irrité-e
    suspicieux-se
    soupçonneux-se
    rancunier-ère
    vindicatif-ve
    combatif-ve
    pugnace
    menaçant-e
    LA PEUR
    (avec être)
    inquièt-e
    nerveux-se
    angoissé-e
    anxieux-se
    paniqué-e
    préoccupé-e
    courageux-se
    mort-e de peur
    soucieux-se de
    apeuré-e
    effrayé-e
    terrifié-e
    horrifié-e
    fébrile
    """

    emotions_mots = {}
    emotion_courante = None

    for ligne in texte.split('\n'):
        ligne = ligne.strip()
        if ligne in ["LE BONHEUR", "LE MALHEUR", "LA COLÈRE", "LA PEUR"]:
            emotion_courante = ligne.lower().replace("le ", "").replace("la ", "")
            emotions_mots[emotion_courante] = []
        elif ligne and emotion_courante and not ligne.startswith('('):
            mots = re.split(r'[,()]', ligne)[0].strip()
            emotions_mots[emotion_courante].append(mots)

    return emotions_mots

# Utilisation de la fonction
resultat = extraire_emotions_mots()

# Affichage du résultat
for emotion, mots in resultat.items():
    print(f"{emotion.capitalize()}:")
    for mot in mots:
        print(f"  - {mot}")
    print()